#!/bin/bash
set -eu

# ===============================
# Load .env if exists
# ===============================
ENV_FILE="/etc/amneziawg/.env"
if [ -f "$ENV_FILE" ]; then
    set -o allexport
    . "$ENV_FILE"
    set +o allexport
fi

# ===============================
# Path constants
# ===============================
: "${WG_DIR:=/etc/amneziawg}"
: "${TMP_DIR:=/tmp/amneziawg}"
: "${CLIENT_PEERS_DIR:=$WG_DIR/client_peers}"
: "${SERVER_PEERS_DIR:=$WG_DIR/server_peers}"
: "${CONFIG_DB:=$WG_DIR/config.json}"
: "${WG_CONF_FILE:=wg0.conf}"
: "${KEYS_DIR:=$WG_DIR/keys}"
: "${WG_LOGFILE:=/var/log/amneziawg/amneziawg.log}"
: "${LOG_LEVEL:=INFO}"

# ===============================
# WireGuard defaults
# ===============================
: "${WG_MODE:=server}"
: "${WG_IFACE:=wg0}"
: "${WG_ADDRESS:=10.100.0.1/24}"
: "${WG_PORT:=13440}"
: "${WG_ENDPOINT:=}"
: "${WG_PEER_COUNT:=1}"

# ===============================
# Proxy defaults
# ===============================
: "${PROXY_ENABLED:=true}"
: "${PROXY_PORT_HTTP:=3128}"
: "${PROXY_PORT_SOCKS5:=4128}"
: "${PROXY_LOG_DIR:=/var/log/3proxy}"

# ===============================
# Junk / obfuscation values
# ===============================
: "${Jc:=3}"
: "${Jmin:=1}"
: "${Jmax:=50}"
: "${S1:=25}"
: "${S2:=72}"
: "${H1:=1411927821}"
: "${H2:=1212681123}"
: "${H3:=1327217326}"
: "${H4:=1515483925}"
: "${UDP_SIGNATURE:=DEFAULT}"
declare -A PROTOCOL_MAP=(
    ["DEFAULT"]="<b >"
    ["DNS"]="<b 0xd4ca6d64afbc2c56dcd32984080045000034cd5a00004011a3ffc0a80202b9248d90ab79003500200991c70701000001000000000000037777770272750000010001>"
    ["QUIC"]="<b >"
)

# ===============================
# Monitoring
# ===============================
: "${MON_CHECK_IP:=9.9.9.9}"
: "${MON_CHECK_INTERVAL:=10}"
: "${MON_CHECK_TIMEOUT:=10}"

# ===============================
# Export all variables
# ===============================
export WG_DIR TMP_DIR CLIENT_PEERS_DIR SERVER_PEERS_DIR CONFIG_DB WG_CONF_FILE WG_LOGFILE KEYS_DIR LOG_LEVEL
export WG_MODE WG_IFACE WG_ADDRESS WG_PORT WG_ENDPOINT WG_PEER_COUNT
export PROXY_ENABLED PROXY_PORT_HTTP PROXY_PORT_SOCKS5 PROXY_LOG_DIR
export Jc Jmin Jmax S1 S2 H1 H2 H3 H4 UDP_SIGNATURE
export MON_CHECK_IP MON_CHECK_INTERVAL MON_CHECK_TIMEOUT PROTOCOL_MAP
